(2329, 'aamc-pcrs-comp-c0100', '1 Patient Care', 'Provide patient-centered care that is compassionate, appropriate, and effective for the treatment of health problems and the promotion of health', NULL, 2328, 9, NULL, 0, 0, 1, 0, 2, NULL, 0, 1391798786, 1),
(2330, 'aamc-pcrs-comp-c0200', '2 Knowledge for Practice', 'Demonstrate knowledge of established and evolving biomedical, clinical, epidemiological and social-behavioral sciences, as well as the application of this knowledge to patient care', NULL, 2328, 9, NULL, 1, 0, 1, 0, 2, NULL, 0, 1391798786, 1),
(2331, 'aamc-pcrs-comp-c0300', '3 Practice-Based Learning and Improvement', 'Demonstrate the ability to investigate and evaluate oneÃƒÂ¢??s care of patients, to appraise and assimilate scientific evidence, and to continuously improve patient care based on constant self-evaluation and life-long learning', NULL, 2328, 9, NULL, 2, 0, 1, 0, 2, NULL, 0, 1391798786, 1),
(2332, 'aamc-pcrs-comp-c0400', '4 Interpersonal and Communication Skills', 'Demonstrate interpersonal and communication skills that result in the effective exchange of information and collaboration with patients, their families, and health professionals', NULL, 2328, 9, NULL, 3, 0, 1, 0, 2, NULL, 0, 1391798786, 1),
(2333, 'aamc-pcrs-comp-c0500', '5 Professionalism', 'Demonstrate a commitment to carrying out professional responsibilities and an adherence to ethical principles', NULL, 2328, 9, NULL, 4, 0, 1, 0, 2, NULL, 0, 1391798786, 1),
(2334, 'aamc-pcrs-comp-c0600', '6 Systems-Based Practice', 'Demonstrate an awareness of and responsiveness to the larger context and system of health care, as well as the ability to call effectively on other resources in the system to provide optimal health care', NULL, 2328, 9, NULL, 5, 0, 1, 0, 2, NULL, 0, 1391798786, 1),
(2335, 'aamc-pcrs-comp-c0700', '7 Interprofessional Collaboration', 'Demonstrate the ability to engage in an interprofessional team in a manner that optimizes safe, effective patient- and population-centered care', NULL, 2328, 9, NULL, 6, 0, 1, 0, 2, NULL, 0, 1391798786, 1),
(2336, 'aamc-pcrs-comp-c0800', '8 Personal and Professional Development', 'Demonstrate the qualities required to sustain lifelong personal and professional growth', NULL, 2328, 9, NULL, 7, 0, 1, 0, 2, NULL, 0, 1391798786, 1),
(2337, 'aamc-pcrs-comp-c0101', '1.1', 'Perform all medical, diagnostic, and surgical procedures considered', NULL, 2329, 9, NULL, 0, 0, 1, 0, 2, NULL, 0, 1391798786, 1),
(2338, 'aamc-pcrs-comp-c0102', '1.2', 'Gather essential and accurate information about patients and their conditions through history-taking, physical examination, and the use of laboratory data, imaging, and other tests', NULL, 2329, 9, NULL, 1, 0, 1, 0, 2, NULL, 0, 1391798786, 1),
(2339, 'aamc-pcrs-comp-c0103', '1.3', 'Organize and prioritize responsibilities to provide care that is safe, effective, and efficient', NULL, 2329, 9, NULL, 2, 0, 1, 0, 2, NULL, 0, 1391798786, 1),
(2340, 'aamc-pcrs-comp-c0104', '1.4', 'Interpret laboratory data, imaging studies, and other tests required for the area of practice', NULL, 2329, 9, NULL, 3, 0, 1, 0, 2, NULL, 0, 1391798786, 1),
(2341, 'aamc-pcrs-comp-c0105', '1.5', 'Make informed decisions about diagnostic and therapeutic interventions based on patient information and preferences, up-to-date scientific evidence, and clinical judgment', NULL, 2329, 9, NULL, 4, 0, 1, 0, 2, NULL, 0, 1391798786, 1),
(2342, 'aamc-pcrs-comp-c0106', '1.6', 'Develop and carry out patient management plans', NULL, 2329, 9, NULL, 5, 0, 1, 0, 2, NULL, 0, 1391798786, 1),
(2343, 'aamc-pcrs-comp-c0107', '1.7', 'Counsel and educate patients and their families to empower them to participate in their care and enable shared decision making', NULL, 2329, 9, NULL, 6, 0, 1, 0, 2, NULL, 0, 1391798786, 1),
(2344, 'aamc-pcrs-comp-c0108', '1.8', 'Provide appropriate referral of patients including ensuring continuity of care throughout transitions between providers or settings, and following up on patient progress and outcomes', NULL, 2329, 9, NULL, 7, 0, 1, 0, 2, NULL, 0, 1391798786, 1),
(2345, 'aamc-pcrs-comp-c0109', '1.9', 'Provide health care services to patients, families, and communities aimed at preventing health problems or maintaining health', NULL, 2329, 9, NULL, 8, 0, 1, 0, 2, NULL, 0, 1391798786, 1),
(2346, 'aamc-pcrs-comp-c0110', '1.10', 'Provide appropriate role modeling', NULL, 2329, 9, NULL, 9, 0, 1, 0, 2, NULL, 0, 1391798786, 1),
(2347, 'aamc-pcrs-comp-c0111', '1.11', 'Perform supervisory responsibilities commensurate with one\'s roles, abilities, and qualifications', NULL, 2329, 9, NULL, 10, 0, 1, 0, 2, NULL, 0, 1391798786, 1),
(2348, 'aamc-pcrs-comp-c0199', '1.99', 'Other patient care', NULL, 2329, 9, NULL, 11, 0, 1, 0, 2, NULL, 0, 1391798786, 1),
(2349, 'aamc-pcrs-comp-c0201', '2.1', 'Demonstrate an investigatory and analytic approach to clinical situations', NULL, 2330, 9, NULL, 0, 0, 1, 0, 2, NULL, 0, 1391798786, 1),
(2350, 'aamc-pcrs-comp-c0202', '2.2', 'Apply established and emerging bio-physical scientific principles fundamental to health care for patients and populations', NULL, 2330, 9, NULL, 1, 0, 1, 0, 2, NULL, 0, 1391798786, 1),
(2351, 'aamc-pcrs-comp-c0203', '2.3', 'Apply established and emerging principles of clinical sciences to diagnostic and therapeutic decision-making, clinical problem-solving, and other aspects of evidence-based health care', NULL, 2330, 9, NULL, 2, 0, 1, 0, 2, NULL, 0, 1391798786, 1),
(2352, 'aamc-pcrs-comp-c0204', '2.4', 'Apply principles of epidemiological sciences to the identification of health problems, risk factors, treatment strategies, resources, and disease prevention/health promotion efforts for patients and populations', NULL, 2330, 9, NULL, 3, 0, 1, 0, 2, NULL, 0, 1391798786, 1),
(2353, 'aamc-pcrs-comp-c0205', '2.5', 'Apply principles of social-behavioral sciences to provision of patient care, including assessment of the impact of psychosocial and cultural influences on health, disease, care-seeking, care compliance, and barriers to and attitudes toward care', NULL, 2330, 9, NULL, 4, 0, 1, 0, 2, NULL, 0, 1391798786, 1),
(2354, 'aamc-pcrs-comp-c0206', '2.6', 'Contribute to the creation, dissemination, application, and translation of new health care knowledge and practices', NULL, 2330, 9, NULL, 5, 0, 1, 0, 2, NULL, 0, 1391798786, 1),
(2355, 'aamc-pcrs-comp-c0299', '2.99', 'Other knowledge for practice', NULL, 2330, 9, NULL, 6, 0, 1, 0, 2, NULL, 0, 1391798786, 1),
