(2357, 'aamc-pcrs-comp-c0302', '3.2', 'Set learning and improvement goals', NULL, 2331, 9, NULL, 1, 0, 1, 0, 2, NULL, 0, 1391798786, 1),
(2358, 'aamc-pcrs-comp-c0303', '3.3', 'Identify and perform learning activities that address one\'s gaps in knowledge, skills, and/or attitudes', NULL, 2331, 9, NULL, 2, 0, 1, 0, 2, NULL, 0, 1391798786, 1),
(2359, 'aamc-pcrs-comp-c0304', '3.4', 'Systematically analyze practice using quality improvement methods, and implement changes with the goal of practice improvement', NULL, 2331, 9, NULL, 3, 0, 1, 0, 2, NULL, 0, 1391798786, 1),
(2360, 'aamc-pcrs-comp-c0305', '3.5', 'Incorporate feedback into daily practice', NULL, 2331, 9, NULL, 4, 0, 1, 0, 2, NULL, 0, 1391798786, 1),
(2361, 'aamc-pcrs-comp-c0306', '3.6', 'Locate, appraise, and assimilate evidence from scientific studies related to patients health problems', NULL, 2331, 9, NULL, 5, 0, 1, 0, 2, NULL, 0, 1391798786, 1),
(2362, 'aamc-pcrs-comp-c0307', '3.7', 'Use information technology to optimize learning', NULL, 2331, 9, NULL, 6, 0, 1, 0, 2, NULL, 0, 1391798786, 1),
(2363, 'aamc-pcrs-comp-c0308', '3.8', 'Participate in the education of patients, families, students, trainees, peers and other health professionals', NULL, 2331, 9, NULL, 7, 0, 1, 0, 2, NULL, 0, 1391798786, 1),
(2364, 'aamc-pcrs-comp-c0309', '3.9', 'Obtain and utilize information about individual patients, populations of patients, or communities from which patients are drawn to improve care', NULL, 2331, 9, NULL, 8, 0, 1, 0, 2, NULL, 0, 1391798786, 1),
(2365, 'aamc-pcrs-comp-c0310', '3.10', 'Continually identify, analyze, and implement new knowledge, guidelines, standards, technologies, products, or services that have been demonstrated to improve outcomes', NULL, 2331, 9, NULL, 9, 0, 1, 0, 2, NULL, 0, 1391798786, 1),
