(2368, 'aamc-pcrs-comp-c0402', '4.2', 'Communicate effectively with colleagues within one\'s profession or specialty, other health professionals, and health related agencies (see also 7.3)', NULL, 2332, 9, NULL, 1, 0, 1, 0, 2, NULL, 0, 1391798786, 1),
(2369, 'aamc-pcrs-comp-c0403', '4.3', 'Work effectively with others as a member or leader of a health care team or other professional group (see also 7.4)', NULL, 2332, 9, NULL, 2, 0, 1, 0, 2, NULL, 0, 1391798786, 1),
(2370, 'aamc-pcrs-comp-c0404', '4.4', 'Act in a consultative role to other health professionals', NULL, 2332, 9, NULL, 3, 0, 1, 0, 2, NULL, 0, 1391798786, 1),
(2371, 'aamc-pcrs-comp-c0405', '4.5', 'Maintain comprehensive, timely, and legible medical records', NULL, 2332, 9, NULL, 4, 0, 1, 0, 2, NULL, 0, 1391798786, 1),
(2372, 'aamc-pcrs-comp-c0406', '4.6', 'Demonstrate sensitivity, honesty, and compassion in difficult conversations, including those about death, end of life, adverse events, bad news, disclosure of errors, and other sensitive topics', NULL, 2332, 9, NULL, 5, 0, 1, 0, 2, NULL, 0, 1391798786, 1),
(2373, 'aamc-pcrs-comp-c0407', '4.7', 'Demonstrate insight and understanding about emotions and human responses to emotions that allow one to develop and manage interpersonal interactions', NULL, 2332, 9, NULL, 6, 0, 1, 0, 2, NULL, 0, 1391798786, 1),
