INSERT INTO `medbiq_assessment_methods` (`assessment_method_id`, `code`, `assessment_method`, `assessment_method_description`, `active`, `updated_date`, `updated_by`) VALUES
(1, 'AM001', 'Clinical Documentation Review', 'The review and assessment of clinical notes and logs kept by learners as part of practical training in the clinical setting (Bowen & Smith, 2010; Irby, 1995)', 1, 0, 0),
(2, 'AM002', 'Clinical Performance Rating/Checklist', 'A non-narrative assessment tool (checklist, Likert-type scale, other instrument) used to note completion or\rachievement of learning tasks (MacRae, Vu, Graham, Word-Sims, Colliver, & Robbs, 1995; Turnbull, Gray, & MacFadyen, 1998) also see ?Direct Observations or Performance Audits,? Institute for International Medical Education, 2002)', 1, 0, 0),
(3, 'AM003', 'Exam - Institutionally Developed, Clinical Performance', 'Practical performance-based examination developed internally to assess problem solving, clinical reasoning, decision making, and[/or] communication skills (LCME, 2011) (Includes observation of learner or small group by instructor)', 1, 0, 0),
(4, 'AM004', 'Exam - Institutionally Developed, Written/Computer-based', 'Examination utilizing various written question-and-answer formats (multiple-choice, short answer, essay, etc.) which may assess learners\' factual knowledge retention; application of knowledge, concepts, and principles; problem-solving acumen; and clinical reasoning (Cooke, Irby, & O?Brien, 2010b; LCME, 2011)', 1, 0, 0),
(5, 'AM005', 'Exam - Institutionally Developed, Oral', 'Verbal examination developed internally to assess problem solving, clinical reasoning, decision making, and[/or] communication skills (LCME, 2011)', 1, 0, 0),
(6, 'AM006', 'Exam - Licensure, Clinical Performance', 'Practical, performance-based examination developed by a professional licensing body to assess clinical skills such as problem solving, clinical reasoning, decision making, and communication, for licensure to practice in a given jurisdiction (e.g., USMLE for the United States); typically paired with a written/computer-based component (MCC, 2011a & 2011c; NBOME, 2010b; USMLE, n.d.); may also be used by schools to assess learners? achievement of certain curricular objectives', 1, 0, 0),
(7, 'AM007', 'Exam - Licensure, Written/Computer-based', 'Standardized written examination administered to assess learners\' factual knowledge retention; application of knowledge, concepts, and principles; problem-solving acumen; and clinical reasoning, for licensure to practice in a given jurisdiction (e.g., USMLE for the United States); typically paired with a clinical performance component (MCC, 2011a & 2011b; NBOME, 2010b; USMLE, n.d.); may also be used by schools or learners themselves to assess achievement of certain curricular objectives', 1, 0, 0),
(8, 'AM008', 'Exam - Nationally Normed/Standardized, Subject', 'Standardized written examination administered to assess learners? achievement of nationally established educational expectations for various levels of training and/or specialized subject area(s) (e.g., NBME Subject or ?Shelf? Exam) (NBME, 2011; NBOME, 2010a)', 1, 0, 0),
(9, 'AM009', 'Multisource Assessment', 'A formal assessment of performance by supervisors, peers, patients, and coworkers (Bowen & Smith, 2010; Institute for International Medical Education, 2002) (Also see Peer Assessment)', 1, 0, 0),
(10, 'AM010', 'Narrative Assessment', 'An instructor\'s or observer\'s written subjective assessment of a learner\'s work or performance (Mennin, McConnell, & Anderson, 1997); May Include: Comments within larger assessment; Observation of learner or small group by instructor', 1, 0, 0),
(11, 'AM011', 'Oral Patient Presentation', 'The presentation of clinical case (patient) findings, history and physical, differential diagnosis, treatment plan, etc., by a learner to an instructor or small group, and subsequent discussion with the instructor and/or small group for the purposes of learner demonstrating skills in clinical reasoning, problem-solving, etc.\r(Wiener, 1974)', 1, 0, 0),
(12, 'AM012', 'Participation', 'Sharing or taking part in an activity (Education Resources Information Center, 1966b)', 1, 0, 0),
(13, 'AM013', 'Peer Assessment', 'The concurrent or retrospective review by learners of the quality and efficiency of practices or services ordered or performed by fellow learners (based on MeSH Scope Note for \"Peer Review, Health Care,\" U.S. National Library of Medicine, 1992)', 1, 0, 0),
(14, 'AM014', 'Portfolio-Based Assessment', 'Review of a learner\'s achievement of agreed-upon academic objectives or completion of a negotiated set of learning activities, based on a learner portfolio (Institute for International Medical Education, 2002) (\"a systematic collection of a student\'s work samples, records of observation, test results, etc., over a period of time\"? Education Resources Information Center, 1994)', 1, 0, 0),
(15, 'AM015', 'Practical (Lab)', 'Learner engagement in hands-on or simulated exercises in which they collect or use data to test and/or verify hypotheses or to address questions about principles and/or phenomena (LCME, 2011)', 0, 1588743484, 1),
(16, 'AM016', 'Research or Project Assessment', 'Assessment of activities and outcomes (e.g., posters, presentations, reports, etc.) of a project in which the learner participated or conducted research (Dyrbye, Davidson, & Cook, 2008)', 1, 0, 0),
(17, 'AM017', 'Self-Assessment', 'The process of evaluating one?s own deficiencies, achievements, behavior or professional performance and competencies (Institute for International Medical Education, 2002); Assessment completed by the learner to reflect and critically assess his/her own performance against a set of established criteria (Gordon, 1991) (NOTE: Does not refer to NBME Self-Assessment)', 1, 0, 0),
(18, 'AM018', 'Stimulated Recall', 'The use of various stimuli (e.g., written records, audio tapes, video tapes) to re-activate the experience of a learner during a learning activity or clinical encounter in order to reflect on task performance, reasoning, decision-making, interpersonal skills, personal thoughts and feelings, etc. (Barrows, 2000)', 1, 0, 0),
(19, 'AM019', 'Exam - Institutionally Developed, Laboratory Practical (Lab)', 'Examination activities that use hands-on or simulated exercises in which students collect or use data to test and/or verify hypotheses or to address questions about principles and/or phenomena.', 1, 1588743484, 1);

